import React, { useState } from "react";

/**Import core component (variable)*/
import Select from "../../core/Select";
import { TOP_100_FILMS } from "./films";

export const DEMO = ({
  type,
  view,
  color,
  icon,
  placeholder,
  fill,
  centerAlign,
  disabled,
  filterable,
  matchTargetWidth
}) => {

  const initialValue = "holla";
  const [item, setItem] = useState(initialValue);

  console.log(placeholder, item)

  const doSomething = item => {
    // Use the onActiveItemChange prop
  }

  return (
    <Select
      type={type}
      view={view}
      color={color}
      icon={icon === true ? "film" : null}
      placeholder={placeholder ? "Select a film..." : null}
      fill={fill}
      centerAlign={centerAlign}
      filterable={filterable}
      disabled={disabled}
      matchTargetWidth={matchTargetWidth}
      items={TOP_100_FILMS}
      selectedItem={item}
      onItemSelect={setItem}
      // onActiveItemChange={() => doSomething()}
    />
  );
};
