import React from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router";

import Logo from "../assets/candy-ui-logo.svg";

import Button from "../core/Button";
import { CustomButton } from "./components/CustomButton";
import Navbar from "../core/Navbar";
import SearchInput from "../core/Inputs/SearchInput";
import NavbarGroup from "../core/Navbar/Group.jsx";
import NavbarHeading from "../core/Navbar/Heading.jsx";

import DemoAppStyle from "./styles/style.module.css";
import * as ROUTES from "./constants/Routes.jsx";

import { ThemeContext } from "../core/ThemeContext";
import { SidebarStateContext } from "./context/SidebarContext";

const AppBar = props => {
  const { device, location } = props;

  let showFabButton =
    location.pathname === "/docs/colors"
      ? false
      : location.pathname === "/docs/installation"
      ? false
      : location.pathname === "/docs/introduction"
      ? false
      : location.pathname === "/docs/implementation"
      ? false
      : location.pathname === "/docs/principles"
      ? false
      : location.pathname === "/"
      ? false
      : true;

  return (
    <ThemeContext.Consumer>
      {({ isDark, setDark }) => (
        <SidebarStateContext.Consumer>
          {({ leftSidebarState, rightSidebarState, setSidebarState }) => (
            <React.Fragment>
              {showFabButton && (
                <div className={DemoAppStyle.props_button}>
                  <Button
                    type="circle"
                    view="filled"
                    icon={"cog"}
                    color="primary"
                    onClick={() =>
                      setSidebarState({
                        left: leftSidebarState,
                        right: !rightSidebarState
                      })
                    }
                  />
                </div>
              )}

              
<Navbar type="desktop" view="smooth" color="primary">

// Menu Button
<NavbarGroup align="left">
  <NavbarHeading>
    <Hamburger
      type="circle"
      view="outlined"
      color="primary"
      size={20}
      variant="slider"
      isOpen={isOpenCondition}
      setOpen={() => someFunc()}
    />
  </NavbarHeading>
</NavbarGroup>

// Primary Nav Buttons
<NavbarGroup align="center" style={{ marginLeft: "1.5rem" }}>
  <Link to="/dashboard">
    <Button
      type="default"
      view="smooth"
      color="primary"
      text="Dashboard"
      active={location.pathname.match("dashboard") && true}
      dense={type === "desktop" ? false : true}
    />
  </Link>
  <Link to="/messages">
    <Button
      type="default"
      view="smooth"
      color="primary"
      text="Messages"
      active={location.pathname.match("messages") && true}
      dense={type === "desktop" ? false : true}
    />
  </Link>
  <Link to="/notifications">
    <Button
      type="default"
      view="smooth"
      color="primary"
      text="Notifications"
      active={location.pathname.match("notifications") && true}
      dense={type === "desktop" ? false : true}
    />
  </Link>
</NavbarGroup>

// Action Buttons
<NavbarGroup align="right">
  <Link to="/sign-in">
    <Button
      type="default"
      view="smooth"
      color="primary"
      text="Sign In"
      style={{ marginLeft: "1.5rem" }}
      active={location.pathname.match("sign-in") && true}
      dense={type === "desktop" ? false : true}
    />
  </Link>
  <Link to="/register">
    <Button
      type="default"
      view="filled"
      color="primary"
      text="Register"
      style={{ marginLeft: "1rem" }}
      active={location.pathname.match("register") && true}
      dense={type === "desktop" ? false : true}
    />
  </Link>
</NavbarGroup>
</Navbar>

            </React.Fragment>
          )}
        </SidebarStateContext.Consumer>
      )}
    </ThemeContext.Consumer>
  );
};

export default withRouter(AppBar);
