import React from "react";
import cx from "classnames";
import PropTypes from "prop-types";

/**Import core component (variable)*/
import Button from "../../core/Button";
import Menu from "../../core/Menu";
import MenuItem from "../../core/Menu/MenuItem";
import { filterItem, renderItem, renderMenuList } from "./SelectFunctions";
import { Select as SelectSource } from "@blueprintjs/select";

import Type from "./_type.module.css";
import View from "./_view.module.css";
import Color from "../../styles/color.module.css";
import { ThemeContext } from "../ThemeContext";

export default function Select(props) {
  const {
    type = "default",
    view = "filled",
    color = "default",
    items,
    ...restProps
  } = props;

  const [item, setItem] = React.useState(items[0]);

  const renderItem  = (
    item,
    { handleClick, modifiers, query }
  ) => {
    if (!modifiers.matchesPredicate) {
      return null;
    }
    const text = `${item.rank}. ${item.title}`;
    return (
      <MenuItem
        active={modifiers.active}
        disabled={modifiers.disabled}
        label={item.year.toString()}
        key={item.rank}
        onClick={handleClick}
        text={highlightText(text, query)}
      />
    );
  };
  
  const renderMenuList = ({ items, itemsParentRef, query, renderItem }) => {
    const renderedItems = items.map(renderItem).filter(item => item != null);
    return (
        <Menu
          type="default"
          view="smooth"
          color="primary"
          ulRef={itemsParentRef}
          style={{ maxHeight: "400px", overflow: "auto" }}
        >
          { renderedItems.length < 1 && 
            <MenuItem
              type="default"
              view="smooth"
              color="primary"
              disabled={true}
              text={`No results matching "${query}"`}
            />
          }
          {renderedItems}
        </Menu>
    );
  };
  
  const filterItem = (query, item) => {
    return (
      `${item.rank}. ${item.title.toLowerCase()} ${item.year}`.indexOf(
        query.toLowerCase()
      ) >= 0
    );
  };
  
  const highlightText = (text, query) => {
    let lastIndex = 0;
    const words = query
      .split(/\s+/)
      .filter((word) => word.length > 0)
      .map(escapeRegExpChars);
    if (words.length === 0) {
      return [text];
    }
    const regexp = new RegExp(words.join("|"), "gi");
    const tokens = [];
    while (true) {
      const match = regexp.exec(text);
      if (!match) {
        break;
      }
      const length = match[0].length;
      const before = text.slice(lastIndex, regexp.lastIndex - length);
      if (before.length > 0) {
        tokens.push(before);
      }
      lastIndex = regexp.lastIndex;
      tokens.push(<strong key={lastIndex}>{match[0]}</strong>);
    }
    const rest = text.slice(lastIndex);
    if (rest.length > 0) {
      tokens.push(rest);
    }
    return tokens;
  }
  
  const escapeRegExpChars = text => {
    return text.replace(/([.*+?^=!:${}()|\[\]\/\\])/g, "\\$1");
  }
  
  const itemSelectProps = {
    itemPredicate: filterItem,
    itemRenderer: renderItem,
    itemListRenderer: renderMenuList
  };

  return (
    <ThemeContext.Consumer>
      {({ isDark }) => (
        <SelectSource
          type={type}
          view={view}
          color={color}
          itemPredicate={filterItem}
          itemRenderer={renderItem}
          itemListRenderer={renderMenuList}
          onItemSelect={setItem}
          items={items}
          {...restProps}
        >
          <Button
            type={type}
            view={view}
            color={color}
            text={item.title}
            rightIcon="caret-down"
          />
        </SelectSource>
      )}
    </ThemeContext.Consumer>
  );
}

Select.propTypes = {
  /**
   `The type of the component.
   * Variants: `card` `action` `circle` `icon`
   * Default value (if undefined): `default` `
   */
  type: PropTypes.oneOf(["default", "card", "action", "circle", "icon"]),
  /**
   ` The view of the component.
   * Variants: `flat` `smooth` `outlined` `raised`
   * Default value (if undefined): `filled` `
   */
  view: PropTypes.oneOf(["filled", "flat", "smooth", "outlined", "raised"]),
  /**
  ` The color of the component.
   * Variants: `primary` `warning` `danger` `success` `primaryAlt` `warningAlt` `dangerAlt` `successAlt`
   * Default value (if undefined): `default` `
   */
  color: PropTypes.oneOf([
    "default",
    "primary",
    "warning",
    "danger",
    "success",
    "primary_alt",
    "warning_alt",
    "danger_alt",
    "success_alt"
  ]),
  /**
   * Click event handler.
   */
  onClick: PropTypes.func,
  /**
   * Whether this component should expand to fill its container.
   */
  fill: PropTypes.bool,
  /**
   * Name of a Blueprint UI icon (or an icon element) to render before the text.
   */
  icon: PropTypes.any,
  /**
   * Name of a Blueprint UI icon (or an icon element) to render after the text.
   */
  rightIcon: PropTypes.any,
  /**
   * If set to `true`, the button will display a centered loading spinner instead of its contents.
   * The width of the button is not affected by the value of this prop.
   */
  loading: PropTypes.bool,
  /**
   * If `true`, the button will be disabled.
   */
  disabled: PropTypes.bool,
  /**
   * Dense size
   */
  dense: PropTypes.bool
};
