import React from "react";
import { Overview } from "../../OverviewComponent";
import { OverviewTemplate } from "../../OverviewText";

export const API = props => {
  const {
    name,
    type,
    view,
    color,
    icon,
    rightIcon,
    fill,
    dis,
    dense,
    allowSingleDayRange
  } = props;

  /**Overview */
  const OverviewText = <OverviewTemplate name={name} />;

  /**API */
  const APIData = [
    {
      prop: "type",
      type: (
        <>
          string = <i>default</i>
        </>
      ),
      desc: (
        <>
          This prop set shape of the component. Possible states:{" "}
          <b> default | card | action | circle | icon</b>. Default state is{" "}
          <b>default</b>.
        </>
      )
    },
    {
      prop: "view",
      type: (
        <>
          tring = <i>filled</i>
        </>
      ),
      desc: (
        <>
          {" "}
          This prop set view of the component. Possible states:{" "}
          <b> filled | flat | smooth | outlined | raised </b>. Default state is{" "}
          <b>filled</b>.
        </>
      )
    },
    {
      prop: "color",
      type: (
        <>
          string = <i>default</i>
        </>
      ),
      desc: (
        <>
          {" "}
          This prop set color of the component. Possible states:{" "}
          <b>
            {" "}
            default | primary | warning | danger | success | primary_alt |
            warning_alt | danger_alt | success_alt{" "}
          </b>
          . Default state is <b>default</b>.
        </>
      )
    },
    {
      prop: "className",
      type: "string",
      desc:
        "A space-delimited list of class names to pass along to a child element."
    },
    {
      prop: "text",
      type: "string",
      desc: <>Action text. Havn't default state.</>
    },
    {
      prop: "dense",
      type: "boolean",
      desc: <>Whether this button should use minimal styles.</>
    },
    {
      prop: "fill",
      type: (
        <>
          boolean = <i>false</i>
        </>
      ),
      desc: (
        <>
          Whether this button should expand to fill its container. Default state
          is <b>false</b>.
        </>
      )
    },
    {
      prop: "icon",
      type: "string ",
      desc: (
        <>
          Name of a Blueprint UI icon to render before the text. For example,{" "}
          <b>"tick"</b>
        </>
      )
    },
    {
      prop: "rightIcon",
      type: "string | element",
      desc: (
        <>
          Name of a Blueprint UI icon (or an icon element) to render before the
          text.
        </>
      )
    },
    {
      prop: "disabled",
      type: (
        <>
          boolean = <i>false</i>
        </>
      ),
      desc: (
        <>
          If set to true, the button will be disabled. Default state is{" "}
          <b>false</b>
        </>
      )
    },
    {
      prop: "onClick",
      type: "func",
      desc: <>Click event handler.</>
    },
    {
      prop: "elementRef",
      type: "any",
      desc: (
        <>
          A ref handler that receives the native HTML element backing this
          component.
        </>
      )
    }
  ];

  /** Code source */
  const Code = `  
  < DateRangePicker
      <i>type</i> = '${type}' 
      <i>view</i> = '${view}' 
      <i>color</i> = '${color}' 
      <i>dense</i> = {${dense ? "true" : "false"}}
      ${type !== "circle" && type !== "icon" ? "<i>text</i> = 'Show code'" : ""}
      <i>icon</i> = ${
        type === "circle" || type === "icon" || icon === true
          ? "'plus'"
          : "{false}"
      }
      <i>rightIcon</i> = ${
        type !== "circle" &&
        type !== "card" &&
        type !== "icon" &&
        rightIcon === true
          ? "'history'"
          : "{false}"
      }
      <i>disabled</i> = ${dis ? "{true}" : "{false}"}
      <i>fill</i> = ${fill ? "{true}" : "{false}"}
  />
 `;

  return <Overview overview={OverviewText} apidata={APIData} code={Code} />;
};
