import React, { useRef, useState } from "react";
import cx from "classnames";
import PropTypes from "prop-types";

/**Import core component (variable)*/
import { MultiSelect as MultiSelectSource } from "@blueprintjs/select";
import { Icon } from "@blueprintjs/core";
import SearchInput from "../../../core/Inputs/SearchInput";
import InputGroup from "../../../core/Inputs/SearchInput";
import Button from "../../../core/Button";
import Menu from "../../../core/Menu";
import MenuItem from "../../../core/Menu/MenuItem";
import View from "./_view.module.css";
import Color from "../../../styles/color.module.css";
import SearchType from "../../../core/Inputs/SearchInput/_type.module.css";
import SearchView from "../../../core/Inputs/SearchInput/_view.module.css";
import MenuType from "../../../core/Menu/_type.module.css";
import MenuView from "../../../core/Menu/_view.module.css";
import { ThemeContext } from "../../ThemeContext";

export default function Select(props) {
  const {
    type = "def",
    view = "filled",
    color = "default",
    disabled = "false",
    icon,
    fill,
    centerAlign,
    customWidth,
    matchTargetWidth,
    items,
    placeholder,
    selectedItem,
    onItemSelect,
    onActiveItemChange,
    ...restProps
  } = props;

  const [createdItems, setCreatedItems] = useState();
  const [stateItems, setStateItems] = useState(items);
  const [selections, setSelections] = useState([]);

  console.log(stateItems)

  const renderItem  = (
    item,
    { handleClick, modifiers, query }
  ) => {
    if (!modifiers.matchesPredicate) {
      return null;
    }
    const text = `${item.id}. ${item.title}`;
    return (
      <MenuItem
        type={type}
        view={view}
        color={color}
        active={modifiers.active}
        disabled={modifiers.disabled}
        label={item.label ? item.label.toString() : null}
        key={item.id}
        onClick={handleClick}
        text={highlightText(text, query)}
      />
    );
  };
  
  const renderMenuList = ({ items, itemsParentRef, query, renderItem }) => {
    const renderedItems = items.map(renderItem).filter(item => item != null);
    return (
      <Menu
        type={type}
        view={view === "outlined" ? "raised" : view}
        color={color}
        ulRef={itemsParentRef}
        disabled={disabled}
        style={{ maxHeight: "390px", overflow: "auto" }}
      >
        { renderedItems.length < 1 && 
          <MenuItem
            type={type}
            view={view}
            color={color}
            disabled={true}
            text={`No results matching "${query}"`}
          />
        }
        {renderedItems}
      </Menu>
    );
  };
  
  const filterItem = (query, item) => {
    return (
      `${item.id}. ${item.title.toLowerCase()} ${item.label}`.indexOf(
        query.toLowerCase()
      ) >= 0
    );
  };
  
  const highlightText = (text, query) => {
    let lastIndex = 0;
    const words = query
      .split(/\s+/)
      .filter((word) => word.length > 0)
      .map(escapeRegExpChars);
    if (words.length === 0) {
      return [text];
    }
    const regexp = new RegExp(words.join("|"), "gi");
    const tokens = [];
    while (true) {
      const match = regexp.exec(text);
      if (!match) {
        break;
      }
      const length = match[0].length;
      const before = text.slice(lastIndex, regexp.lastIndex - length);
      if (before.length > 0) {
        tokens.push(before);
      }
      lastIndex = regexp.lastIndex;
      tokens.push(<strong key={lastIndex}>{match[0]}</strong>);
    }
    const rest = text.slice(lastIndex);
    if (rest.length > 0) {
      tokens.push(rest);
    }
    return tokens;
  }
  
  const escapeRegExpChars = text => {
    return text.replace(/([.*+?^=!:${}()|\[\]\/\\])/g, "\\$1");
  }

  const handleTagRemove = (tag, index) => {
    deselectItem(index);
  };

  const getSelectedItemIndex = item => {
      return items.indexOf(item);
  }

  const selectItem = item => {
    selectItems([item]);
  }

  const selectItems = itemsToSelect => {
    let nextCreatedItems = createdItems.slice();
    let nextItems = items.slice();
    let nextSelections = selections.slice();

    itemsToSelect.forEach(item => {
        const results = maybeAddCreatedItemToArrays(nextSelections, nextCreatedItems, item);
        nextSelections = results.selections;
        nextCreatedItems = results.createdItems;
        // Avoid re-creating an item that is already selected (the "Create
        // Item" option will be shown even if it matches an already selected
        // item).
        nextItems = !arrayContainsItem(nextItems, item) ? [...nextItems, item] : nextItems;
    });

    setCreatedItems(nextCreatedItems);
    setStateItems(nextItems);
    setSelections(nextSelections);
  }

  const deselectItem = (index) => {
    const item = stateItems[index];
    const { createdItems: nextCreatedItems, items: nextSelections } = maybeDeleteCreatedItemFromArrays(
        stateItems,
        createdItems,
        item,
    );

    // Delete the item if the user manually created it.
    setCreatedItems(nextCreatedItems);
    setStateItems(stateItems.filter((item, i) => i !== index));
    setSelections(nextSelections);
  }

  const isItemSelected = item => {
    return getSelectedItemIndex(item) !== -1;
  }

  const handleItemSelect = item => {
    if (!isItemSelected(item)) {
        selectItem(item);
    } else {
        deselectItem(getSelectedItemIndex(item));
    }
  };

  const arrayContainsItem = (stateItems, itemToFind) => {
    console.log(stateItems)
      return stateItems.some((item) => item.title === itemToFind.title);
  }

  const addItemToArray = (stateItems, itemToAdd) => {
      return [...stateItems, itemToAdd];
  }

  const deleteItemFromArray = (stateItems, itemToDelete) => {
      return stateItems.filter(item => item !== itemToDelete);
  }
  const maybeAddCreatedItemToArrays = (items, createdItems, item) => {
      const isNewlyCreatedItem = !arrayContainsItem(items, item);
      return {
          createdItems: isNewlyCreatedItem ? addItemToArray(createdItems, item) : createdItems,
          // Add a created item to `items` so that the item can be deselected.
          stateItems: isNewlyCreatedItem ? addItemToArray(items, item) : items,
      };
  }

  const maybeDeleteCreatedItemFromArrays =  (stateItems, createdItems, item) => {
      const wasItemCreatedByUser = arrayContainsItem(createdItems, item);

      // Delete the item if the user manually created it.
      return {
          createdItems: wasItemCreatedByUser ? deleteItemFromArray(createdItems, item) : createdItems,
          stateItems: wasItemCreatedByUser ? deleteItemFromArray(stateItems, item) : stateItems,
      };
  }

  const handleClear = () => setSelections([]);

  const clearButton =
    selections && selections.length > 0 ? <Button icon="cross" minimal={true} onClick={handleClear} /> : undefined;

  const renderTag = item => item.title;

  return (
    <ThemeContext.Consumer>
      {({ isDark }) => (
        <MultiSelectSource
          tagInputProps={{ placeholder: placeholder }}
          itemRenderer={renderItem}
          itemListRenderer={renderMenuList}
          tagRenderer={renderTag}
          items={stateItems}
          selectedItems={[]}
          // onItemSelect={onItemSelect}
          onItemSelect={handleItemSelect}
          inputProps={{
            fill: true,
            leftIcon:
              <Icon
                icon={"search"}
                iconSize={type === "dense" ? 16 : 20}
                className={SearchView["searchButton"]}
                style={type === "dense" ? { margin: "0.4rem" } : { margin: "0.65rem" }}
              />,
            className: cx(
              SearchType[type],
              SearchView[view],
              Color[color]
            )
          }}
          tagInputProps={{
            onRemove: handleTagRemove,
            rightElement: clearButton
          }}
          popoverProps={{
            minimal: true,
            popoverClassName: cx(
              View["select-popover"],
              MenuView[view],
              MenuType[type],
              Color[color]
            )
          }}
          {...restProps}
        >
        {/* // <MultiSelectSource
        //   type={type === "dense" ? "dense" : "default"}
        //   view={view}
        //   color={color}
        //   fill={fill}
        //   disabled={disabled}
        //   filterable={false}
        //   matchTargetWidth={matchTargetWidth}
        //   itemPredicate={filterItem}
        //   itemRenderer={renderItem}
        //   itemListRenderer={renderMenuList}
        //   onItemSelect={onItemSelect}
        //   onActiveItemChange={onActiveItemChange}
        //   items={items}
        //   inputProps={{
        //     fill: true,
        //     leftIcon:
        //       <Icon
        //         icon={"search"}
        //         iconSize={type === "dense" ? 16 : 20}
        //         className={SearchView["searchButton"]}
        //         style={type === "dense" ? { margin: "0.4rem" } : { margin: "0.65rem" }}
        //       />,
        //     className: cx(
        //       SearchType[type],
        //       SearchView[view],
        //       Color[color]
        //     )
        //   }}
        //   popoverProps={{
        //     minimal: true,
        //     popoverClassName: cx(
        //       View["select-popover"],
        //       MenuView[view],
        //       MenuType[type],
        //       Color[color]
        //     )
        //   }}
        //   {...restProps}
        // > */}
          {/* <InputGroup
            type={type}
            view={view}
            color={color}
            placeholder="Search for something..."
            fill={fill}
            // onSearch={onSearch}
          /> */}
        </MultiSelectSource>
      )}
    </ThemeContext.Consumer>
  );
}

Select.propTypes = {
  /**
   `The type of the component.
   * Variants: `card` `action` `circle` `icon`
   * Default value (if undefined): `default` `
   */
  type: PropTypes.oneOf(["default", "card", "action", "circle", "icon"]),
  /**
   ` The view of the component.
   * Variants: `flat` `smooth` `outlined` `raised`
   * Default value (if undefined): `filled` `
   */
  view: PropTypes.oneOf(["filled", "flat", "smooth", "outlined", "raised"]),
  /**
  ` The color of the component.
   * Variants: `primary` `warning` `danger` `success` `primaryAlt` `warningAlt` `dangerAlt` `successAlt`
   * Default value (if undefined): `default` `
   */
  color: PropTypes.oneOf([
    "default",
    "primary",
    "warning",
    "danger",
    "success",
    "primary_alt",
    "warning_alt",
    "danger_alt",
    "success_alt"
  ]),
  /**
   * Item select event listener.
   */
  onItemSelect: PropTypes.func,
  /**
   * Whether this component should expand to fill its container.
   */
  fill: PropTypes.bool,
  /**
   * Name of a Blueprint UI icon (or an icon element) to render before the text.
   */
  icon: PropTypes.any,
  /**
   * If set to `true`, the button will display a centered loading spinner instead of its contents.
   * The width of the button is not affected by the value of this prop.
   */
  loading: PropTypes.bool,
  /**
   * If `true`, the button will be disabled.
   */
  disabled: PropTypes.bool,
  /**
   * Whether target button text should be centerd
   */
  centerAlign: PropTypes.bool
};
