import React, { useState } from "react";

/**Import core component (variable)*/
import MultiSelect from "../../core/Select/SelectMulti";
import { TOP_100_FILMS } from "./films";

export const DEMO = ({
  type,
  view,
  color,
  icon,
  fill,
  centerAlign,
  disabled,
  matchTargetWidth
}) => {

  const [item, setItem] = useState();

  const newItemObj = {
    title,
    label: new Date().getFullYear(),
  };

  return (
    <MultiSelect
      type={type}
      view={view}
      color={color}
      icon={icon === true ? "film" : null}
      placeholder="Select a film..."
      fill={fill}
      centerAlign={centerAlign}
      allowCreate={true}
      disabled={disabled}
      matchTargetWidth={matchTargetWidth}
      items={TOP_100_FILMS}
      selectedItem={item}
      onItemSelect={setItem}
      itemMap={newItemObj}
      // onActiveItemChange={() => doSomething()}
    />
  );
};
