import React from "react";

/**Import core component (variable)*/
import Button from "../../core/Button";
import MenuItem from "../../core/Menu/MenuItem";
import { filterFilm, renderFilm, renderMenuList, TOP_100_FILMS } from "./films";
import { Select } from "@blueprintjs/select";
import ExampleMenu from "./Menu";

export const DEMO = ({ view, color, dense }) => {
  const [film, setFilm] = React.useState(TOP_100_FILMS[0]);
  return (
    <Select
      type="default"
      view="smooth"
      color="primary"
      view={view}
      color={color}
      items={TOP_100_FILMS}
      filterable={true}
      itemPredicate={filterFilm}
      itemRenderer={renderFilm}
      itemListRenderer={renderMenuList}
      onItemSelect={setFilm}
    >
      <Button text={film.title} rightIcon="caret-down" />
    </Select>
  );
};
