import React from "react";

/**Import core component (variable)*/
import Select from "../../core/Select";
import MenuItem from "../../core/Menu/MenuItem";
import { ItemPredicate, ItemRenderer } from "@blueprintjs/select";
import ExampleMenu from "./Menu";

export const DEMO = ({ view, color, dense }) => {
  const Films = [
    { title: "The Shawshank Redemption", year: 1994 },
    { title: "The Godfather", year: 1972 },
    { title: "The Godfather: Part II", year: 1974 },
  ];
  const highlightText = () => {
    let lastIndex = 0;
    const words = query
      .split(/\s+/)
      .filter((word) => word.length > 0)
      .map(escapeRegExpChars);
    if (words.length === 0) {
      return [text];
    }
    const regexp = new RegExp(words.join("|"), "gi");
    const tokens = [];
    while (true) {
      const match = regexp.exec(text);
      if (!match) {
        break;
      }
      const length = match[0].length;
      const before = text.slice(lastIndex, regexp.lastIndex - length);
      if (before.length > 0) {
        tokens.push(before);
      }
      lastIndex = regexp.lastIndex;
      tokens.push(<strong key={lastIndex}>{match[0]}</strong>);
    }
    const rest = text.slice(lastIndex);
    if (rest.length > 0) {
      tokens.push(rest);
    }
    return tokens;
  };

  const filterFilm = (query, film) => {
    return (
      `${film.rank}. ${film.title.toLowerCase()} ${film.year}`.indexOf(
        query.toLowerCase()
      ) >= 0
    );
  };
  const renderFilm  = (
    film,
    { handleClick, modifiers, query }
  ) => {
    if (!modifiers.matchesPredicate) {
      return null;
    }
    const text = `${film.rank}. ${film.title}`;
    return (
      <MenuItem
        active={modifiers.active}
        disabled={modifiers.disabled}
        label={film.year.toString()}
        key={film.rank}
        onClick={handleClick}
        text={highlightText(text, query)}
      />
    );
  };
  return (
    <Select
      view={view}
      color={color}
      items={Films}
      itemPredicate={filterFilm}
      itemRenderer={renderFilm}
      noResults={<MenuItem disabled={true} text="No results." />}
    />
  );
};
