import React from "react";

/**Import core component (variable)*/
import { Select } from "../../core/Select";

export const DEMO = ({ view, color, dense }) => {
  const [item, setItem] = React.useState(TOP_100_FILMS[0]);
  return (
    <Select
      view={view}
      color={color}
      items={TOP_100_FILMS}
      filterable={true}
      itemPredicate={filterItem}
      itemRenderer={renderItem}
      itemListRenderer={renderMenuList}
      onItemSelect={setItem}
    >
      <Button text={item.title} rightIcon="caret-down" />
    </Select>
  );
};
