import React from "react";

/**Import core component (variable)*/
import Button from "../../core/Button";
import { filterOption, renderOption, renderMenuList, TOP_100_FILMS } from "./films";
import { Select } from "@blueprintjs/select";

export const DEMO = ({ view, color, dense }) => {
  const [option, setOption] = React.useState(TOP_100_FILMS[0]);
  return (
    <Select
      view={view}
      color={color}
      options={TOP_100_FILMS}
      filterable={true}
      optionPredicate={filterOption}
      optionRenderer={renderOption}
      optionListRenderer={renderMenuList}
      onOptionSelect={setFilm}
    >
      <Button text={option.title} rightIcon="caret-down" />
    </Select>
  );
};
