import React from "react";

/**Import core component (variable)*/
import Toaster from "../../core/Toast/Toaster.jsx";
import Button from "../../core/Button";
import { Icon } from "@blueprintjs/core";

export const DEMO = props => {
  const { view, color, isIcon, isClose, DemoToast } = props;

  /** Store ref*/
  const [myRef, setRef] = React.useState(null);

  return (
    <div>
      <Button
        view="filled"
        color="primary"
        text="Toast me"
        onClick={() => myRef.show(DemoToast)}
      />
      <Toaster
        view={view}
        color={color}
        usePortal={false}
        position="top" //bottom, bottom-left, bottom-right
        // icon={isIcon ? <Icon icon="info-sign" iconSize={24} /> : null}
        setRef={setRef}
        //ref={ref => setRef(ref)}
        maxToasts={2}
        withoutClose={!isClose}
      />
    </div>
  );
};
